CREATE TABLE `jobsearch`.`emp_details` ( `sr_no` INT(4) NULL AUTO_INCREMENT , `date` DATE NOT NULL DEFAULT (CURRENT_DATE) , `f_name` VARCHAR(255) NOT NULL , `l_name` VARCHAR(255) NOT NULL , `gender` VARCHAR(10) NOT NULL , `email` VARCHAR(255) NOT NULL , `mobile` VARCHAR(10) NOT NULL , `dob` VARCHAR(12) NOT NULL , `address` VARCHAR(255) NOT NULL , `a_qualification` VARCHAR(255) NOT NULL , `g_certification` VARCHAR(255) NOT NULL , `y_experience` VARCHAR(2) NOT NULL , `c_company` VARCHAR(255) NOT NULL , `l_company` VARCHAR(255) NOT NULL , `location` VARCHAR(255) NOT NULL , `n_period` TEXT NOT NULL , `h_technology` VARCHAR(500) NOT NULL , PRIMARY KEY(`sr_no`) );
CREATE TABLE jobsearch.`job_details` ( `sr_no` INT(4) AUTO_INCREMENT PRIMARY KEY , `date` DATE NOT NULL DEFAULT (CURRENT_DATE) , `job_title` VARCHAR(150) NOT NULL , `company` VARCHAR(100) NOT NULL , `location` VARCHAR(100) NOT NULL , `technology` VARCHAR(150) , `description` TEXT ,  `email` VARCHAR(100) NOT NULL , `salary` VARCHAR(50) );
